mapper.select:
SELECT a.product_code, a.product_name, a.product_price, a.product_description, b.category_code as "category.category_code", b.category_name as "category.category_name", a.product_image_url, a.product_stock FROM tbl_product a JOIN tbl_category b ON (a.category_code = b.category_code) WHERE status = 'USABLE'
AND a.category_code = #{ categoryCode }
AND a.product_name LIKE CONCAT('%', #{ productName }, '%')
ORDER BY a.product_code DESC LIMIT #{ limit } OFFSET #{ offset }

SELECT COUNT(*) FROM tbl_product WHERE status = 'USABLE'
AND category_code = #{ categoryCode }
AND product_name LIKE CONCAT('%', #{ productName }, '%')
